null_bytes.c<pre><code>u32 zero = 0;
void reset_context2(struct tail_context* c){

    __builtin_memset(c, 0, 2); // reset only 2 bytes

    struct null_bytes* nb = (struct null_bytes*)bpf_lookup_elem(&amp;null_bytes_map, &amp;zero);
    if(!nb){
        return;
    }
    // write null_bytes into payload
    bpf_probe_read_kernel(c-&gt;payload, 512, nb-&gt;data);
}
</code></pre> maps.c<pre><code>// -------&gt; sample tail-context
struct tail_context {
    bool f1;
    bool f2;
    u8 payload[512];
}

struct {
     __uint(type, BPF_MAP_TYPE_PERCPU_ARRAY);
     __type(key, u32);
     __type(value, struct tail_context);
     __uint(max_entries, 1);
} tail_ctx_map SEC(".maps");
// --------


// data from below map is read only for resetting maps
struct null_bytes {
  u8 data[8192]; // maximum null_bytes
};

struct {
  __uint(type, BPF_MAP_TYPE_ARRAY);
  __type(key, u32);
  __type(value, struct null_bytes);
  __uint(max_entries, 1);
  __uint(map_flags,
         BPF_F_RDONLY_PROG | BPF_F_RDONLY); // makes the map read-only
} null_bytes_map SEC(".maps");
</code></pre>

programs.h<pre><code>#include "maps.h"

u8 ZERO = 0;


SEC("cgroup_skb/egress")
long egress3(struct __sk_buff* ctx){
    bpf_printk("[egress3] Someone called me");

    // Query tail_context
    struct tail_context* ctx2 = (struct tail_context*)bpf_map_lookup_elem(&amp;tail_context_map, &amp;ZERO);
    if(!ctx){
        return SK_PASS;
    }

    // Use tail_context values
    bpf_printk("[egress3] first=%d second=%d", ctx2-&gt;value_from_1, ctx2-&gt;value_from_1);

    return SK_PASS;
}

SEC("cgroup_skb/egress")
long egress2(struct __sk_buff* ctx){


    // Query tail context
    struct tail_context* ctx2 = (struct tail_context*)bpf_map_lookup_elem(&amp;tail_context_map, &amp;ZERO);
    if(!ctx){
        return SK_PASS;
    }

    // Populate tail_context
    ctx2-&gt;value_from_2 = 22;

    bpf_printk("[egress2] Calling egress3");
    bpf_tail_call(ctx, &amp;tail_programs, TAIL_CALL_EGRESS_3)
    return SK_PASS;
}


// Initial function
SEC("cgroup_skb/egress")
long egress1(struct __sk_buff* ctx){


    // Query tail context
    struct tail_context* ctx2 = (struct tail_context*)bpf_map_lookup_elem(&amp;tail_context_map, &amp;ZERO);
    if(!ctx){
        return SK_PASS;
    }

    // Reset tail context
    __builtin_memset(ctx2, 0, sizeof(struct tail_context));

    ctx2-&gt;value_from_1 = 11;

    bpf_printk("[egress1] Calling egress2");
    bpf_tail_call(ctx, &amp;tail_programs, TAIL_CALL_EGRESS_2)
    return SK_PASS;
}
</code></pre> maps.h<pre><code>// declare the index in array to use for tail-called function
#define TAIL_CALL_EGRESS_2 0 
#define TAIL_CALL_EGRESS_3 1 


// declare the prototype of function to be stored
long egress2(struct __sk_buff* ctx);
long egress3(struct __sk_buff* ctx);


// create map &amp; initialize values
struct {
    __uint(type, BPF_MAP_TYPE_PROG_ARRAY);
    __uint(max_entries, 1);
    __type(key, __u32);
    __array(values, long(struct __sk_buff* ctx));
} tail_programs SEC(".maps") = {
    .values = {
        [TAIL_CALL_EGRESS_2] = (void *)&amp;egress2, 
        [TAIL_CALL_EGRESS_3] = (void *)&amp;egress3,


    },
};  

// Custom tail_contxt
struct tail_context{
    u8 value_from_1;
    u8 value_from_2;
}

// Every CPU has its own private copy of this map
struct {
    __uint(type, BPF_MAP_TYPE_PERCPU_ARRAY);
    __uint(max_entries, 1);
    __type(key, __u32);
    __type(value, struct tail_context);
} tail_context_map SEC(".maps") 
</code></pre>

